import React, { useState, useEffect, useRef } from 'react';
import { Form, Input, Button, message,Popconfirm } from 'antd';
import axios from 'axios';
const TaskAddList = ({userData}) => {
    const [veri, setVeri] = useState([]); // Initialize veri state with data prop or an empty array
    const apiUrl = 'https://v1.nocodeapi.com/gizem00001/google_sheets/xsWcTNrhFFLBFSqR?tabId=login';

    useEffect(() => {
        fetchData(); // Call fetchData function on component mount
    }, []); // Empty dependency array ensures this effect runs once on component mount
    const fetchData = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;
            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            setVeri(filteredUsers);
        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
        }
    };

    const handleDelete = async (task) => {
        console.log('Sil:', task);

        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;


            // Silinecek task'in kimliğini (row_id) al
            const deletedData = allData.find(item => item.row_id !== row_id );
            if (!deletedData) {
                throw new Error('Görev bulunamadı.'); // Eğer görev bulunamazsa hata fırlat
            }

            const row_id = deletedData.row_id; // Görevin kimliği (row_id)

            // Veritabanından silme işlemi için DELETE isteği gönder
            await axios.delete(`https://v1.nocodeapi.com/gizem00001/google_sheets/xsWcTNrhFFLBFSqR?tabId=login&row_id=${row_id}`);

            // Başarılı bir şekilde silindiğinde bildirim göster
            message.success('Görev başarıyla silindi.');

            // Yeniden veri çekme ve state'i güncelleme
            const updatedData = allData.filter(item => item.Task !== task); // Filter out the deleted task
            setVeri(updatedData); // Update veri state with updated data
        } catch (error) {
            console.error('Görev silinirken hata oluştu:', error);
            message.error('Görev silinirken bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Görevleriniz:</h2>
            {veri && veri.length > 0 ? (
                veri.map((item, index) => (
                    <div key={index} style={{ marginBottom: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Button style={{ flex: 1, marginRight: '10px' }} block>
                            {item}
                        </Button>
                        <Popconfirm
                            title="Bu görevi silmek istediğinizden emin misiniz?"
                            onConfirm={() => handleDelete(item)}
                            okText="Evet"
                            cancelText="Hayır"
                        >
                            <Button type="danger">Sil</Button>
                        </Popconfirm>
                    </div>
                ))
            ) : (
                <p>Yükleniyor...</p>
            )}
        </div>
    );
};

export default TaskAddList;
