import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskEdit = ({ userData }) => {
    const state = {
        name: ''
      }

    const handleChange = event => {
        this.setState({ name: event.target.value });
      }

    cohandleSubmit = event => {
        event.preventDefault();

        const user = {
          name: this.state.name
        };

        axios.post(`https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login`, { user })
          .then(res => {
            console.log(res);
            console.log(res.data);
          })
      }
    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"

                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default TaskEdit;
