import React, { useState, useRef, useEffect } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Task from '../components/task';

const TaskForm = ({ userData }) => {
    const formRef = useRef(null);
    const [name, setName] = useState('');
    const [filteredTasks, setFilteredTasks] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem002/google_sheets/iaBQTHRWCbIvMRlI?tabId=login';

    // Verilerin ilk yüklenmesi ve güncellenmesi için kullanılan fonksiyon
    const fetchDataAndFilterTasks = async () => {
        console.log("veriler filterda");
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            // Kullanıcının verilerini filtrele
            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);


                const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
                setFilteredTasks(tasks);

        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
        }
    };



    // Form içeriğinin değişimi
    const handleChange = (e) => {
        setName(e.target.value);
    };

    // Form submit işlemi
    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];



        try {
            const response = await axios.post(apiUrl, data, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            formRef.current.resetFields(); // Formu temizle
            message.success('Başarıyla Eklendi');
            console.log('Görev başarıyla eklendi:', response.data);

            // Yeni görev ekledikten sonra verileri tekrar getir ve filtrele
            fetchDataAndFilterTasks();
            console.log("veriler yenilendi");
        } catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef}>
                <Form.Item name="taskInput">
                    <Input
                        name="görev"
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                        e
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>

        </div>
    );
};

export default TaskForm;
