import React, { useState } from 'react';
import { Drawer, Button, Space, Avatar } from 'antd';
import { PoweroffOutlined, UserOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';

const Draw = ({ userData, itemsList }) => {
  const navigate = useNavigate();
  const [selectedMenuItem, setSelectedMenuItem] = useState(null);

  const onItemClick = (route) => {
    setSelectedMenuItem(route); // Seçilen menü öğesini işaretle

    // İlgili rotaya yönlendir
    navigate(route);
  };

  const onLogout = () => {
    navigate(-1); // Önceki sayfaya git veya çıkış işlemi yap
  };

  return (
    <Drawer
      title={
        <div style={{ display: 'flex',align:'start',alignItems: 'center' }}>
          <Avatar size="large" icon={<UserOutlined />} />
          <span style={{ marginLeft: '10px', fontSize: '20px', fontWeight: 'bold' }}>
            {userData.User}
          </span>
        </div>
      }
      placement="left"
      closable={false}
      onClose={() => {}} // Kapatma işlemi olmadığı için boş fonksiyon
      visible={true} // Drawer'ı her zaman açık tut
    >
      <Space direction="vertical"style={{ width: '100%' }} align='start'>
        {itemsList.map((item) => (
          <Button
            key={item.key}
            type="text"
            icon={item.icon}
            style={{
              width: '100%',
              textAlign: 'left',
              fontSize: '16px',
              fontWeight: item.route === selectedMenuItem ? 'bold' : 'normal', // Seçilen menü öğesini kalın yap
            }}
            onClick={() => navigate(item.route)}
          >
            {item.label}
          </Button>
        ))}
        <Button
          key="logout"
          type="text"
          icon={<PoweroffOutlined />}
          style={{ width: '100%', textAlign: 'left', fontSize: '16px' }}
          onClick={onLogout}
        >
          Çıkış
        </Button>
      </Space>
    </Drawer>
  );
};

export default Draw;
