import React from 'react';
import { useLocation } from 'react-router-dom';
import Draw from '../common/drawer';
import { Button } from 'antd';
const Task = ({userData}) => {

  const tasks = userData.Task.split(',');

  return (
   <>

   <div style={{padding: '20px' }}>
      <h2>Görevleriniz:</h2>
      <Button>
        {tasks.map((task, index) => (
          <li key={index}>{task.trim()}</li> // Her görevi bir liste öğesi olarak göster, trim() ile boşlukları temizle
        ))}
      </Button>
    </div>
   </>
  );
};

export default Task;
