import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskForm = ({ userData }) => {
  const [name, setName] = useState('');
  const apiUrl = 'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login';

  const handleChange = (e) => {
    setName(e.target.value);
  };

  const handleSubmit = async () => {
    const data = [
      [
        userData.Email,
        userData.Password,
        userData.User,
        name,
        userData.UserID
      ]
    ];

    try {
      await axios.post(
        apiUrl,
        data,
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );
      console.log('Görev başarıyla eklendi');
      setName(''); // Input alanını temizle
      message.success('Görev başarıyla eklendi'); // Başarı mesajı göster
    } catch (error) {
      console.error('Görev eklenirken hata oluştu:', error);
      message.error('Görev eklenirken hata oluştu'); // Hata mesajı göster
    }
  };

  return (
    <div>
      <Form onFinish={handleSubmit}>
        <Form.Item
          name="taskInput"
          rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
        >
          <Input
            name='görev'
            placeholder="Görev Ekleyin"
            value={name}
            onChange={handleChange}
            size="large"
          />
        </Form.Item>
        <Form.Item>
          <Button
            htmlType="submit"
            type="primary"
            icon={<FileAddOutlined />}
            size="large"
          >
            Ekle
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default TaskForm;
