import React, { useRef, useState,useEffect } from 'react';
import {message, ConfigProvider } from 'antd';
import axios from 'axios';
import Login from './login';
const loginData=()=>{
    const formRef = useRef(null);
    const [data, setData] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem11/google_sheets/ICsbCoIOoLbnWrHj?tabId=login';
    const handleLogin = async (values) => {
        const { Email, myPassword } = values;

        // Veri kaynağından alınan verileri kontrol et
        const user = data.find(item => item.Email === Email && item.Password === myPassword);

        if (user) {
            message.success('Başarılı giriş');
            formRef.current.resetFields();
        } else {
            message.error('Hatalı email veya şifre');
        }
    };

    // Veri kaynağından verileri getiren fonksiyon
    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setData(response.data.data);
        } catch (error) {
            console.error('Veri alınamadı', error);
        }
    };

    // Component yüklendiğinde verileri getir
    useEffect(() => {
        getData();
    }, []);
    return(
        <Login handleLogin={handleLogin}></Login>
    );

}
export default loginData;
