import React, { useState, useEffect } from 'react';
import { Form, Input, Button } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Task from '../components/task';

const TaskForm = ({ userData }) => {
    const [name, setName] = useState('');

    const apiUrl = 'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login';

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];

        try {
            const response = await axios.post(
                apiUrl,
                data,
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );
            console.log('Görev başarıyla eklendi:', response.data);


        } catch (error) {
            if (error.response) {
                console.error('Görev eklenirken hata oluştu:', error.response.data);
            } else {
                console.error('Görev eklenirken hata oluştu:', error.message);
            }
        }
    };

    // Görevleri API'den yeniden çekme
    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setTasks(response.data.data); // Görevleri state'e kaydet
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData(); // Component yüklendiğinde ve güncellendiğinde görevleri çek
    }, []);

    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        suffix={<FileAddOutlined />}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>

            </Form>
            {/* Sadece Task bileşenini tasks state'i değiştiğinde yeniden render et */}
            <Task userData={userData}></Task>
        </div>
    );
};

export default TaskForm;
