import React, { useState } from 'react';
import { Drawer, Button, Space } from 'antd';
import { PoweroffOutlined } from '@ant-design/icons'; // Import PoweroffOutlined icon

const Draw = ({ itemsList, handleLogout }) => {
  const [open, setOpen] = useState(true); // Default to open state
  const [placement, setPlacement] = useState('left'); // Default placement 'left'

  const onChange = (e) => {
    setPlacement(e.target.value);
  };

  return (
    <>
      <Button type="primary" onClick={() => setOpen(true)}>
        Open Drawer
      </Button>
      <Drawer
        title="Basic Drawer"
        placement={placement}
        closable={false} // Prevent closing the drawer
        onClose={() => setOpen(false)} // Function to close the drawer
        visible={open}
        key={placement}
      >
        {itemsList.map((item) =>
          item.children ? (
            <Space key={item.key} direction="vertical">
              {item.children.map((child) => (
                <Space key={child.key} direction="vertical">
                  {child.icon && React.createElement(child.icon)}
                  {child.label}
                </Space>
              ))}
              <Button key="logout" icon={<PoweroffOutlined />} onClick={handleLogout}>
                Çıkış (Logout)
              </Button>
            </Space>
          ) : (
            <Space key={item.key} direction="vertical">
              {item.icon && React.createElement(item.icon)}
              {item.label}
            </Space>
          )
        )}
      </Drawer>
    </>
  );
};

export default Draw;
