// src/components/Login.js

import React from 'react';
import { Typography, Form, Input, Button, ConfigProvider } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import LoginData from './useLoginData'; // Import the custom hook
import './login.css'; // Import your CSS file

const Login = () => {
    const { formRef, handleLogin } = useLoginData(); // Use the custom hook

    return (
        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#d11ba7',
                    colorPrimaryHover: '#5b10b5',
                    colorIcon: 'black'
                },
                components: {
                    Input: {
                        colorText: '#black',
                    },
                },
            }}
        >
            <div className="appBg">
                <Form className="loginForm" onFinish={handleLogin} ref={formRef}>
                    <Typography.Title style={{ textAlign: 'center', color: '#fff' }}>Welcome</Typography.Title>

                    <Form.Item
                        name="Email"
                        rules={[
                            {
                                required: true,
                                type: 'email',
                                message: 'Please enter a valid email address.'
                            }
                        ]}
                    >
                        <Input placeholder="Email" suffix={<UserOutlined />} size="large" />
                    </Form.Item>

                    <Form.Item
                        name="myPassword"
                        rules={[
                            {
                                required: true,
                                message: 'Please enter your password.'
                            }
                        ]}
                    >
                        <Input.Password placeholder="Password" size="large" />
                    </Form.Item>

                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type="primary" htmlType="submit" block size="large">
                            Login
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        </ConfigProvider>
    );
};

export default Login;
