import React from 'react';
import { Drawer, Button, Space, Avatar } from 'antd';
import { PoweroffOutlined, UserOutlined } from '@ant-design/icons';
import LoginData from '../services/logindata';
import { useNavigate } from 'react-router-dom';

const Draw = ({ itemsList }) => {
  // Destructuring values from LoginData hook
  const { loggedInUser, handleLogout } = LoginData();
  const navigate = useNavigate();

  return (
    <Drawer
      title={
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar size="large" icon={<UserOutlined />} /> {/* Display user's profile picture */}
          <span style={{ marginLeft: '10px', fontSize: '18px', fontWeight: 'bold' }}>
            {loggedInUser || 'Guest'} {/* Display user name or default message */}
          </span>
        </div>
      }
      placement="left"
      closable={false}
      mask={false}
      onClose={() => {}} // No-op function to prevent closing on ESC key
      visible={true} // Make sure this is controlled based on your application's state
    >
      <Space direction="vertical" style={{ width: '100%' }}>
        {itemsList.map((item) => (
          <Space key={item.key} style={{ marginBottom: '10px' }}>
            {React.cloneElement(item.icon, { style: { fontSize: '20px' } })}
            <span style={{ fontSize: '16px' }}>{item.label}</span>
          </Space>
        ))}

        <Button
          key="logout"
          type="primary"
          icon={<PoweroffOutlined />}
          style={{ width: '100%', marginTop: 'auto', fontSize: '16px' }}
          onClick={() => {
            handleLogout();
            navigate(-1); // Navigate to previous page or perform logout action
          }}
        >
          Çıkış (Logout)
        </Button>
      </Space>
    </Drawer>
  );
};

export default Draw;
