import React, { useState, useEffect } from 'react';
import { Button } from 'antd';
import axios from 'axios';

const Task = ({ userData }) => {
  const [data, setData] = useState([]);
  const apiUrl = 'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm';

  const getData = async () => {
    try {
      const response = await axios.get(`${apiUrl}?tabId=login`);
      setData(response.data.data);
    } catch (error) {
      console.error('Failed to fetch data', error);
    }
  };

  useEffect(() => {
    getData(); // Fetch data
  }, []);

  // Kullanıcının görevlerini filtrele ve göster
  const renderUserTasks = (userId) => {
    const filteredUsers = data.filter(item => item.UserID === userId);

    if (filteredUsers.length === 0) {
      return <div>Kullanıcı bulunamadı.</div>;
    }

    return (
      <div style={{ padding: '20px' }}>
        <h2>Görevleriniz:</h2>
        {filteredUsers.map(user => (
          <div key={user.UserID}>

            {user.Task.split(',').map((task, index) => (
              <Button key={index} style={{ marginBottom: '10px'}} block>
                {task.trim()}
              </Button>
            ))}
          </div>
        ))}
      </div>
    );
  };

  return (
    <div>
      {renderUserTasks(userData.UserID)}
    </div>
  );
};

export default Task;
