import React,{useState} from 'react';
import { Button, message, Popconfirm } from 'antd';
import axios from 'axios';

const TaskList = ({ data }) => {
    const[veri,setVeri]=useState(data);
    const apiUrl = 'https://v1.nocodeapi.com/gizem004/google_sheets/rmLuKVNiJbmqpjdA?tabId=login';
    const handleUpdate = (task) => {
        console.log('Güncelle:', task);
        // Güncelleme işlemini burada gerçekleştir (örneğin, bir form gösterilebilir)
    };

    const handleDelete = async (task) => {
        console.log('Sil:', task);

        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            // Silinecek task'in kimliğini (row_id) al
            const deletedData = allData.find(item => item.Task === task);
            if (!deletedData) {
                throw new Error('Görev bulunamadı.'); // Eğer görev bulunamazsa hata fırlat
            }

            const row_id = deletedData.row_id; // Görevin kimliği (row_id)

            // Veritabanından silme işlemi için DELETE isteği gönder
            await axios.delete(`https://v1.nocodeapi.com/gizem004/google_sheets/rmLuKVNiJbmqpjdA?tabId=login&row_id=${row_id}`);

            // Başarılı bir şekilde silindiğinde bildirim göster
            message.success('Görev başarıyla silindi.');
            const resp = await axios.get(apiUrl);
            const all = response.data.data;
            setVeri(all);
        } catch (error) {
            console.error('Görev silinirken hata oluştu:', error);
            message.error('Görev silinirken bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Görevleriniz:</h2>
            {data && data.length > 0 ? (
                data.map((task, index) => (
                    <div key={index} style={{ marginBottom: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Button style={{ flex: 1, marginRight: '10px' }} block>
                            {task}
                        </Button>
                        <div>
                            <Button type="primary" style={{ marginRight: '5px' }} onClick={() => handleUpdate(task)}>Güncelle</Button>
                            <Popconfirm
                                title="Bu görevi silmek istediğinizden emin misiniz?"
                                onConfirm={() => handleDelete(task)}
                                okText="Evet"
                                cancelText="Hayır"
                            >
                                <Button type="danger">Sil</Button>
                            </Popconfirm>
                        </div>
                    </div>
                ))
            ) : (
                <p>"</p>
            )}
        </div>
    );
};

export default TaskList;
