import React, { useState, useEffect } from 'react';
import { ConfigProvider, message, Timeline } from 'antd';
import axios from 'axios';

const TimeLine = ({ userData, apiUrl }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchDataAndFilterTasks = async () => {
      try {
        const response = await axios.get(apiUrl);
        const allData = response.data.data;
        const filteredTasks = allData.filter(item => item.UserID === userData.UserID);
        const tasksWithSubtasks = filteredTasks.map(user => ({
          task: user.Task,
          date: user.Date,
          subtasks: user.SubTask ? user.SubTask.split(',').map(subtask => subtask.trim()) : [],
          subtaskDates: user.SubTaskDateRanges ? user.SubTaskDateRanges.split(',') : []
        }));

        // Sort tasks by their date
        tasksWithSubtasks.sort((a, b) => new Date(a.date) - new Date(b.date));

        // Sort subtasks by their corresponding dates
        tasksWithSubtasks.forEach(task => {
          task.subtasks = task.subtasks.map((subtask, index) => ({
            name: subtask,
            date: task.subtaskDates[index] ? task.subtaskDates[index].trim() : ''
          })).sort((a, b) => new Date(a.date) - new Date(b.date));
        });

        setTasks(tasksWithSubtasks);
      } catch (error) {
        console.error('Failed to fetch data', error);
        message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
      }
    };

    fetchDataAndFilterTasks();
  }, [apiUrl, userData.UserID]);

  return (
    <ConfigProvider
      theme={{
        token: {
          colorPrimary: '#d11ba7',
          colorPrimaryHover: '#5b10b5',
          colorText: "#d11ba7",
        },
      }}
    >
      <div id="contact" className="contact">
        <div className="contentHolder">
          <div>
            <div className="titleHolder">
              <h2>{userData.User} Görev Zaman Çizelgesi</h2>
            </div>
            <div style={{ maxHeight: '700px', overflowY: 'auto' }}>
              <Timeline mode="alternate">
                {tasks.map((taskObj, index) => (
                  <Timeline.Item key={index}>
                    <p style={{ fontWeight: "bold", fontSize: "18px" }}>{taskObj.task}</p>
                    <p>{taskObj.date}</p>
                    <ul style={{ padding: 0 }}>
                      {taskObj.subtasks.map((subtask, subIndex) => (
                        <li key={subIndex} style={{ fontWeight: 'normal', fontSize: '14px' }}>
                          {subtask.name} ({subtask.date})
                        </li>
                      ))}
                    </ul>
                  </Timeline.Item>
                ))}
              </Timeline>
            </div>
          </div>
        </div>
      </div>
    </ConfigProvider>
  );
}

export default TimeLine;
