import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, message } from 'antd';

const TaskList = ({ userData }) => {
    const [tasks, setTasks] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem00001/google_sheets/xsWcTNrhFFLBFSqR?tabId=login';

    useEffect(() => {
        fetchData();
    }, []); // Empty dependency array ensures this effect runs once on component mount

    const fetchData = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            const userTasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));

            setTasks(userTasks);
             // Set loading to false after data is fetched
        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
             // Ensure loading state is set to false on error
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Görevleriniz:</h2>
            { tasks.length > 0 ? (
                tasks.map((task, index) => (
                    <div key={index} style={{ marginBottom: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Button style={{ flex: 1, marginRight: '10px' }} block>
                            {task}
                        </Button>
                    </div>
                ))
            ) : (
                <p>Yükleniyor...</p>
            )}
        </div>
    );
};

export default TaskList;
