import { Button, Popconfirm, message, ConfigProvider, TimePicker } from "antd";
import { FileExcelOutlined, DownOutlined, UpOutlined } from '@ant-design/icons';
import React, { useState } from "react";
import axios from 'axios';

const TaskList = ({ task, deleteTask, userData, apiUrl }) => {
    const [subTask, setSubTask] = useState([]);
    const [showSubTask, setShowSubTask] = useState(false);
    const [selectedSubTask, setSelectedSubTask] = useState(null);
    const [timeRanges, setTimeRanges] = useState({});
    const [taskTimes, setTaskTimes] = useState([]);
    const handleAddTime = async (task) => {
        if (timeRanges[index]) {
            try {
                // Fetch existing task times from the database
                const response = await axios.get(apiUrl);
                const allData = response.data.data;
                const existingData = allData.find(item => item.SubTask === subTask[index]);

                // If task data exists, update its time
                if (existingData) {
                    const updatedTime = existingData.Time ? `${existingData.Time},${timeRanges[index]}` : timeRanges[index];

                    // Update the time in the database
                    await axios.put(`${apiUrl}&row_id=${existingData.row_id}`, {
                        time: updatedTime
                    });

                    // Update UI or state as needed
                    message.success('Görev zamanı başarıyla eklendi.');
                } else {
                    throw new Error('Görev bulunamadı.');
                }
            } catch (error) {
                console.error('Görev zamanı eklenirken hata oluştu:', error);
                message.error('Görev zamanı eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            }
        } else {
            message.warning('Lütfen zaman aralığını seçin.');
        }
    };

    const handleButtonClick = (index) => {
        setSelectedSubTask(index);
    };

    const handleTimeChange = (index, time) => {
        setTimeRanges(prevRanges => ({
            ...prevRanges,
            [index]: time
        }));
    };


    const handleDelete = async (task) => {
        console.log('Sil:', task);

        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;
            // Silinecek task'in kimliğini (row_id) al
            const deletedData = allData.find(item => item.Task === task);
            if (!deletedData) {
                throw new Error('Görev bulunamadı.'); // Eğer görev bulunamazsa hata fırlat
            }

            const row_id = deletedData.row_id; // Görevin kimliği (row_id)

            // Veritabanından silme işlemi için DELETE isteği gönder
            await axios.delete(`${apiUrl}&row_id=${row_id}`);

            // Başarılı bir şekilde silindiğinde bildirim göster
            message.success('Görev başarıyla silindi.');
            deleteTask(deletedData.Task);

        } catch (error) {
            console.error('Görev silinirken hata oluştu:', error);
            message.error('Görev silinirken bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    const handleItemClick = async (task) => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;
            const filteredUsers = allData.filter(item => item.UserID === userData.UserID && item.Task === task);
            const subtasks = filteredUsers.flatMap(user => user.SubTask.split(',').map(SubTask => SubTask.trim()));
            setSubTask(subtasks);
            setShowSubTask(true);
        } catch (error) {
            console.error('Alt görevler alınırken hata oluştu:', error);
            message.error('Alt görev bulunamadı!');
        }
    };

    return (

        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#d11ba7',
                    colorPrimaryHover: '#5b10b5',
                    colorIcon: 'black'
                }
            }}


        >
            <div style={{ display: "flex", flexDirection: "column" }}>
                <div style={{ display: "flex" }}>
                    <Button
                        style={{ color: "black", marginRight: '10px', width: '637px' }}
                        onClick={() => handleItemClick(task)}
                    >
                        {task} {showSubTask ? <UpOutlined /> : <DownOutlined />}
                    </Button>

                    <Popconfirm
                        title="Bu görevi silmek istediğinizden emin misiniz?"
                        onConfirm={() => handleDelete(task)}
                        okText="Evet"
                        cancelText="Hayır"
                    >
                        <Button
                            icon={<FileExcelOutlined />}
                            type="primary"
                            style={{ marginBlockEnd: "10px" }}
                        >
                            Sil
                        </Button>
                    </Popconfirm>
                </div>

                <div>
            {showSubTask && (
                <div style={{ marginLeft: '20px' }}>
                    {subTask.map((subtask, index) => (
                        <div key={index} style={{ marginBottom: '10px' }}>
                            <Button onClick={() => handleButtonClick(index)}>{subtask}</Button>
                            {selectedSubTask === index && (
                                <>
                                <TimePicker.RangePicker
                                    style={{ marginTop: '10px', marginLeft: '10px' }}
                                    onChange={(time) => handleTimeChange(index, time)}
                                />

                                <Button 
                                        style={{ marginLeft: '10px' }}
                                        onClick={handleAddTime}
                                    >
                                        Ekle
                                    </Button>


                                </>

                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
            </div>



        </ConfigProvider>

    );
}

export default TaskList;
