import React, { useEffect, useState } from 'react';
import { Badge, Calendar, message, Space } from 'antd';
import axios from 'axios';

const colors = [
  'pink', 'red', 'yellow', 'orange', 'cyan', 'green', 'blue', 'purple', 'geekblue', 'magenta', 'volcano', 'gold', 'lime',
];

const TaskCalendar = ({ apiUrl, userData }) => {
  const [todos, setTodos] = useState([]);

  const fetchDataAndFilterTasks = async () => {
    try {
      const response = await axios.get(apiUrl);
      const allData = response.data.data;

      // Filter tasks based on UserID and transform tasks into a usable format
      const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
      const tasks = filteredUsers.flatMap(user =>
        user.Task.split(',').map(task => {
          const [date, ...taskDetails] = task.trim().split(':');
          return { date: date.trim(), task: taskDetails.join(':').trim() };
        })
      );

      setTodos(tasks); // Set initial data

    } catch (error) {
      console.error('Failed to fetch data', error);
      message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
    }
  };

  useEffect(() => {
    fetchDataAndFilterTasks();
  }, []);

  const getListData = (value) => {
    const date = value.format('YYYY-MM-DD');
    return todos.filter(todo => todo.date === date);
  };

  const dateCellRender = (value) => {
    const listData = getListData(value);
    return (
      <ul className="events">
        {listData.map((item, index) => (
          <li key={index}>
            <Space direction="vertical">
              <Badge color={colors[index % colors.length]} text={item.task} />
            </Space>
          </li>
        ))}
      </ul>
    );
  };

  const monthCellRender = (value) => {
    // Assuming getMonthData is defined to return data for the month
    const num = getMonthData(value); // Placeholder function
    return num ? (
      <div className="notes-month">
        <section>{num}</section>
        <span>Backlog number</span>
      </div>
    ) : null;
  };

  const cellRender = (current, info) => {
    if (info.type === 'date') return dateCellRender(current);
    if (info.type === 'month') return monthCellRender(current);
    return info.originNode;
  };

  return <Calendar cellRender={cellRender} />;
};

export default TaskCalendar;
