import React, { Component } from 'react';
import { Button } from 'antd';
import TaskData from '../services/taskdata';

class Task extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filteredTasks: [],
        };
    }

    componentDidMount() {
        this.fetchAndFilterTasks();
    }

    fetchAndFilterTasks = async () => {
        const { userData } = this.props;
        const { filteredTasks, refetchDataAndFilter } = TaskData(userData);
        this.setState({ filteredTasks });
    };

    handleRefreshClick = () => {
        const { userData } = this.props;
        const { refetchDataAndFilter } = TaskData(userData);
        refetchDataAndFilter();
    };

    render() {
        const { filteredTasks } = this.state;

        return (
            <div style={{ padding: '20px' }}>
                <h2>Görevleriniz:</h2>
                {filteredTasks.length > 0 ? (
                    filteredTasks.map((task, index) => (
                        <Button key={index} style={{ marginBottom: '10px' }} block>
                            {task}
                        </Button>
                    ))
                ) : (
                    "Yükleniyor..."
                )}

                <Button type="primary" onClick={this.handleRefreshClick}>
                    Yenile
                </Button>
            </div>
        );
    }
}

export default Task;
