import React, { useEffect, useState } from 'react';
import { Badge, Calendar, message } from 'antd';
import axios from 'axios';
import moment from 'moment';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faFlag } from '@fortawesome/free-solid-svg-icons';

const colors = [
  'pink','green', 'purple', 'blue', 'red', 'yellow', 'orange', 'cyan', 'geekblue', 'magenta', 'volcano', 'gold', 'lime',
];

const TaskCalendar = ({ apiUrl, userData }) => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(apiUrl);
        const allData = response.data.data;

        // Filter tasks based on UserID
        const filteredTasks = allData.filter(item => item.UserID === userData.UserID);

        // Transform tasks into required format
        const tasksWithSubtasks = filteredTasks.map(task => ({
          task: task.Task,
          startDate: moment(task.Date, 'DD-MM-YYYY'), // Assuming task date is named Date
          endDate: moment(task.EndDate, 'DD-MM-YYYY'), // Assuming end date is named EndDate
          subtasks: task.SubTask ? task.SubTask.split(',').map(subtask => subtask.trim()) : [],
          subtaskDates: task.SubTaskDateRanges ? task.SubTaskDateRanges.split(',').map(subdate => subdate.trim()) : []
        }));

        setTodos(tasksWithSubtasks);
      } catch (error) {
        console.error('Failed to fetch data:', error);
        message.error(`Veriler alınamadı. Lütfen daha sonra tekrar deneyin. Hata: ${error.message}`);
      }
    };

    fetchData();
  }, [apiUrl, userData]);

  // Function to group tasks by date
  const groupTasksByDate = () => {
    const groupedTasks = {};
    todos.forEach(task => {
      const startDate = moment(task.startDate);
      const endDate = moment(task.endDate);

      for (let date = startDate; date.isSameOrBefore(endDate, 'day'); date.add(1, 'day')) {
        const formattedDate = date.format('DD-MM-YYYY');
        if (!groupedTasks[formattedDate]) {
          groupedTasks[formattedDate] = [];
        }
        groupedTasks[formattedDate].push(task);
      }
    });
    return groupedTasks;
  };

  // Render tasks on calendar
  const renderTasksOnCalendar = value => {
    const groupedTasks = groupTasksByDate();

    const date = value.format('DD-MM-YYYY');
    const tasksForDate = groupedTasks[date] || [];

    return (
      <ul style={{ padding: 0 }}>
        {tasksForDate.map((task, index) => (
          <li key={index} style={{ marginBottom: 4 }}>
            <Badge color={colors[index % colors.length]} text={task.task} />
            {task.subtasks.map((subtask, idx) => (
              <div key={idx} style={{ marginLeft: 20 }}>
                <Badge color={colors[index % colors.length]} text={subtask} />
              </div>
            ))}
            {task.subtaskDates.map((subdate, idx) => (
              <div key={idx} style={{ marginLeft: 20 }}>
                <Badge color={colors[index % colors.length]} text={subdate} />
              </div>
            ))}
            {moment(task.startDate).isSame(moment(date), 'day') && (
              <span style={{ marginLeft: 8, color: 'rgba(0, 0, 0, 0.45)' }}>
                <FontAwesomeIcon icon={faPlay} />
              </span>
            )}
            {moment(task.endDate).isSame(moment(date), 'day') && (
              <span style={{ marginLeft: 8, color: 'rgba(0, 0, 0, 0.45)' }}>
                <FontAwesomeIcon icon={faFlag} />
              </span>
            )}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div>
      <Calendar dateCellRender={renderTasksOnCalendar} />
    </div>
  );
};

export default TaskCalendar;
