import React, { useRef, useState,useEffect } from 'react';
import { Typography, Form, Input, Button, message, ConfigProvider } from 'antd';
import { UserOutlined } from '@ant-design/icons';


const Login = (props) => {
    <loginData handleLogin></loginData>

    const formRef = useRef(null);

    // Google Sheets API'ye erişim için kullanılacak URL


    // Form submit işlemi


    return (
        <ConfigProvider
            theme={{
                token: {
                    colorPrimary: '#d11ba7',
                    colorPrimaryHover: '#5b10b5',
                    colorIcon: 'black'
                },
                components: {
                    Input: {
                        colorText: '#black',
                    },
                },
            }}
        >
            <div className="appBg">
                <Form className="loginForm" onFinish={handleLogin} ref={formRef}>
                    <Typography.Title style={{ textAlign: 'center', color: '#fff' }}>Hoş Geldiniz</Typography.Title>

                    <Form.Item
                        name="Email"
                        rules={[
                            {
                                required: true,
                                type: 'email',
                                message: 'Lütfen geçerli bir email adresi girin.'
                            }
                        ]}
                    >
                        <Input placeholder="Email" suffix={<UserOutlined />} size="large" />
                    </Form.Item>

                    <Form.Item
                        name="myPassword"
                        rules={[
                            {
                                required: true,
                                message: 'Lütfen şifrenizi girin.'
                            }
                        ]}
                    >
                        <Input.Password placeholder="Şifre" size="large" />
                    </Form.Item>

                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type="primary" htmlType="submit" block size="large">
                            Giriş Yap
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        </ConfigProvider>
    );
};

export default Login;
