import React, { useState, useRef } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Task from '../components/task';
import useTaskData from '../services/taskdata';

const TaskForm = ({ userData }) => {
    const { refetchDataAndFilter } = useTaskData(userData);
    const formRef = useRef(null);
    const [name, setName] = useState('');
    const apiUrl = 'https://v1.nocodeapi.com/gizem001/google_sheets/viewGTYYvtJXMfOr?tabId=login';

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];

        try {
            const response = await axios.post(
                apiUrl,
                data,
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );

            formRef.current.resetFields();
            message.success("Başarıyla Eklendi");
            console.log('Görev başarıyla eklendi:', response.data);

            refetchDataAndFilter(); // Yeni görev ekledikten sonra verileri yeniden getir ve filtrele
        } catch (error) {
            message.warning("Ekleme Yapılamadı");
            if (error.response) {
                console.error('Görev eklenirken hata oluştu:', error.response.data);
            } else {
                console.error('Görev eklenirken hata oluştu:', error.message);
            }
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef}>
                <Form.Item
                    name="taskInput"
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>


        </div>
    );
};

export default TaskForm;
