import React, { useState, useRef } from "react";
import { Form, Input, Button, message, DatePicker } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
const TaskForm = ({ addTask, userData, apiUrl }) => {
    const [value, setValue] = useState();
    const [subtask, setSubTask] = useState();
    const formRef = useRef(null);
    const handleSubmit = async () => {
        if (value) {
            try {

                const taskData = [
                    [
                        userData.Email,
                        userData.Password,
                        userData.User,
                        userData.Telephone,
                        userData.Address,
                        userData.UserID,
                        value,
                        subtask
                    ]
                ];

                // Post new task data
                await axios.post(apiUrl, taskData);
                formRef.current.resetFields();
                message.success('Görev başarıyla eklendi.');
                addTask(value);


            }
            catch (error) {
                message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
                console.error('Görev eklenirken hata oluştu:', error);
            }

        }
    }
    return (
        <Form onFinish={handleSubmit} ref={formRef} layout="inline">
            <Form.Item name="taskInput" style={{ flex: 1 }}>
                <Input
                    name="görev"
                    placeholder="Görev Ekleyin"
                    size="large"

                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                />
            </Form.Item>
            <Form.Item name="Input" style={{ flex: 1 }}>
                <Input
                    name="alt görev"
                    placeholder=" Alt Görev Ekleyin"
                    size="large"

                    value={subtask}
                    onChange={(e) => setSubTask(e.target.value)}
                />
            </Form.Item>
            <Form.Item name="dateInput" style={{ flex: 1 }}>
                <DatePicker
                    placeholder="Tarih Seçin"
                    size="large"
                    value={date ? moment(date) : null}
                    onChange={(date) => setDate(date)}
                />
            </Form.Item>
            <Form.Item>
                <Button
                    icon={<FileAddOutlined />}
                    htmlType="submit"
                    type="primary"
                    size="large"
                    style={{ marginBlockEnd: "20px", marginRight: "2px" }}
                >
                    Ekle
                </Button>
            </Form.Item>


        </Form>
    );


}
export default TaskForm;
