import React, { useState } from 'react';
import { Button, Menu, Avatar } from 'antd';
import { UserOutlined, MailOutlined, PoweroffOutlined,SettingOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';

const { SubMenu } = Menu;

const items = [
  {
    key: 'app',
    avatar: <Avatar size={20} icon={<UserOutlined />} />,
    children: [
      {
        label: 'Profil Düzenle',
        key: 'setting:1',
        icon: <SettingOutlined />,
      },
      {
        label: 'Mail yolla',
        key: 'setting:2',
        icon: <MailOutlined />,
      },
    ],
  },
];

const Home = () => {
  const [current, setCurrent] = useState('setting:1'); // Set a default selected key
  const navigate = useNavigate();

  const handleClick = (e) => {
    console.log('click ', e);
    setCurrent(e.key);
  };

  const handleLogout = () => {
    navigate(-1); // Navigate to previous page or perform logout action
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
      <Menu onClick={handleClick} selectedKeys={[current]} mode="horizontal">
        {items.map(item =>
          item.children ? (
            <SubMenu key={item.key} title={item.avatar} icon={item.icon}>
              {item.children.map(child => (
                <Menu.Item key={child.key} icon={child.icon}>
                  {child.label}
                </Menu.Item>
              ))}
              <Menu.Item key="logout" icon={<PoweroffOutlined />} onClick={handleLogout}>
                Çıkış
              </Menu.Item>
            </SubMenu>
          ) : (
            <Menu.Item key={item.key} icon={item.icon}>
              {item.avatar}
            </Menu.Item>
          )
        )}
      </Menu>

    </div>
  );
};

export default Home;
