import React, { useState, useEffect } from "react";
import TaskForm from "../components/taskform";
import TaskList from "/Users/gizem/Desktop/reactproje/src/components/tasklist.js";
import { Form, Input, Button, message } from 'antd';
import axios from 'axios';

const TaskWrapper = ({ userData }) => {
    const [todos, setTodos] = useState([]);



    // Update localStorage whenever todos state changes
    useEffect(() => {
        localStorage.setItem('todos', JSON.stringify(todos));
    }, [todos]);

    const addTask = (todo) => {
        setTodos([
            ...todos,
            todo,
        ]);
    };

    const deleteTask = (task) => {
        setTodos((prevTodos) => prevTodos.filter((todo) => todo !== task));
    };

    return (
        <div>
            <h1>Get Things Done!</h1>
            <TaskForm addTask={addTask} userData={userData} />
            {todos.map((todo, index) =>
                <TaskList
                    key={index}
                    task={todo}
                    deleteTask={deleteTask}
                />
            )}
        </div>
    );
};

export default TaskWrapper;
