import React, { useState, useEffect, useRef } from 'react';
import { Form, Input, Button, message } from 'antd';
import axios from 'axios';
import TaskList from './tasklist';

const TaskForm = ({ userData }) => {
    const formRef = useRef(null);
    const [name, setName] = useState('');
    const [filteredTasks, setFilteredTasks] = useState([]);
    const [fetchingTasks, setFetchingTasks] = useState(false); // State to manage fetching indicator
    const [refreshKey, setRefreshKey] = useState(0); // Key to force TaskList remount
    const apiUrl = 'https://v1.nocodeapi.com/gizem004/google_sheets/rmLuKVNiJbmqpjdA?tabId=login';

    // Function to fetch and filter tasks
    const fetchDataAndFilterTasks = async () => {
        try {
            setFetchingTasks(true); // Set fetching indicator

            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
            setFilteredTasks(tasks);
        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
        } finally {
            setFetchingTasks(false); // Reset fetching indicator
        }
    };

    // Fetch initial data on component mount
    useEffect(() => {
        fetchDataAndFilterTasks();
    }, []);

    // Handle input change
    const handleChange = (e) => {
        setName(e.target.value);
    };

    // Handle form submission
    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];

        try {
            // Post new task data
            await axios.post(apiUrl, data);

            // Clear form fields and notify success
            formRef.current.resetFields();
            setName('');
            message.success('Görev başarıyla eklendi.');

            // Update refresh key to force TaskList remount
            setRefreshKey(prevKey => prevKey + 1);

            // Refetch tasks to update list
            fetchDataAndFilterTasks();
        } catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef}>
                <Form.Item name="taskInput">
                    <Input
                        name="görev"
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        size="large"
                        disabled={!name.trim()} // Disable button if input is empty or only whitespace
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
            <TaskList key={refreshKey} filteredTasks={filteredTasks} fetchingTasks={fetchingTasks} />
        </div>
    );
};

export default TaskForm;
