import React, { useState } from 'react';
import { Form, Input, Button, ConfigProvider } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import Task from '../components/Task';

const TaskEdit = ({ userData }) => {
  const [taskInput, setTaskInput] = useState('');

  const handleTaskInputChange = (e) => {
    setTaskInput(e.target.value);
  };

  const handleAddTask = () => {
    // Perform task addition logic here, e.g., update userData or send to backend
    // For demo purposes, we will log the task to console
    console.log(`Adding task: ${taskInput}`);

    // Clear input field after adding task
    setTaskInput('');
  };

  return (
    <div>
      <Form onFinish={handleAddTask}>
        <Form.Item name="taskInput">
          <Input
            placeholder="GÃ¶rev Ekleyin"
            value={taskInput}
            onChange={handleTaskInputChange}
            suffix={
              <Button htmlType="submit" type="primary" icon={<FileAddOutlined />} size="large">
                Ekle
              </Button>
            }
            size="large"
          />
        </Form.Item>
      </Form>
      <Task userData={userData} />
    </div>
  );
};

export default TaskEdit;
