import React, { useState,useRef } from "react";
import { Form, Input, Button, message } from 'antd';
import axios from 'axios';
const TaskForm=({addTask,userData})=>{
    const[value,setValue]=useState();
    const formRef = useRef(null);
    const apiUrl = 'https://v1.nocodeapi.com/gizem00001/google_sheets/xsWcTNrhFFLBFSqR?tabId=login';
    const handleSubmit= async ()=>{
        if (value) {
            try {
            const taskData = [
                [
                    userData.Email,
                    userData.Password,
                    userData.User,
                    value,
                    userData.UserID
                ]
            ];

            // Post new task data
            await axios.post(apiUrl, taskData);
            formRef.current.resetFields();
            message.success('Görev başarıyla eklendi.');
            addTask(value);

        }
        catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        } 

          }
    }
    return (
        <Form onFinish={handleSubmit} ref={formRef} layout="inline">
            <Form.Item name="taskInput" style={{ flex: 1 }}>
                    <Input
                        name="görev"
                        placeholder="Görev Ekleyin"
                        size="large"
                        value={value}
                        onChange={(e) => setValue(e.target.value)}
                    />
            </Form.Item>
            <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        size="large"

                    >
                        Ekle
                    </Button>
                </Form.Item>

        </Form>
    );


}
export default TaskForm;
