import React, { useState, useRef, useEffect } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Task from '../components/task';
import DefaultPage from '../views/defaultPage';

const TaskForm = ({ userData }) => {

    const formRef = useRef(null);
    const[state,setState]=useState(false);




    // Form içeriğinin değişimi
    const handleChange = (e) => {
        setName(e.target.value);
    };



    // Form submit işlemi
    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];



        try {
            const response = await axios.post(apiUrl, data, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            formRef.current.resetFields(); // Formu temizle
            message.success('Başarıyla Eklendi');
            console.log('Görev başarıyla eklendi:', response.data);

            // Yeni görev ekledikten sonra verileri tekrar getir ve filtrele
            fetchDataAndFilterTasks();
            console.log("veriler yenilendi");
            setState(true);

        } catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        }

    };

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef}>
                <Form.Item name="taskInput">
                    <Input
                        name="görev"
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button

                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"

                    >
                        Ekle
                    </Button>

                </Form.Item>
            </Form>





        </div>
    );
};

export default TaskForm;
