import React, { useState } from 'react';
import { Form, Input, Button } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskForm = ({ userData }) => {
    const [name, setName] = useState('');
    const [data, setData] = useState([]);
    const apiUrl='https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login'
    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setData(response.data.data);
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData(); // Fetch data
    }, []);
    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];

        try {
            const response = await axios.post(
                apiUrl,
                data,
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );
            console.log('Görev başarıyla eklendi:', response.data);
        } catch (error) {
            if (error.response) {
                // Sunucudan gelen yanıt var
                console.error('Görev eklenirken hata oluştu:', error.response.data);
            } else {
                // Yanıt yok, istek hiç yapılmadı ya da yapılamadı
                console.error('Görev eklenirken hata oluştu:', error.message);
            }
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default TaskForm;
