import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskEdit = ({ userData }) => {
    const [form] = Form.useForm();
    const [taskInput, setTaskInput] = useState('');

    const handleTaskInputChange = (e) => {
      setTaskInput(e.target.value);
    };

    const onFinish = async (values) => {
        const data = [
            // This is the outer array
            [
              { Email: "pt@getDefaultNormalizer.com"}, // This is the inner array (object)

              { Task: values.taskInput },

            ],
          ];

        try {
          const response = await axios.post(
            "https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login",
            data
          );
          console.log('İşlem başarılı:', response);
          message.success('İşlem Başarılı');
        } catch (error) {
          console.error('İşlem Başarısız:', error);
          // Access error details (if available)
          const errorMessage = error.response?.data?.message || 'Beklenmeyen bir hata oluştu';
          message.error(errorMessage);
        }
      };

    const onFinishFailed = (errorInfo) => {
      console.log('Failed:', errorInfo);
    };

  return (
    <div>
      <Form
        form={form}
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
      >
        <Form.Item
          name="taskInput"
          rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
        >
          <Input
            placeholder="Görev Ekleyin"
            value={taskInput}
            onChange={handleTaskInputChange}
            size="large"
          />
        </Form.Item>
        <Form.Item>
          <Button
            htmlType="submit"
            type="primary"
            icon={<FileAddOutlined />}
            size="large"
          >
            Ekle
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default TaskEdit;
