import React, { useState, useEffect } from 'react';
import { Button, message, Popconfirm } from 'antd';
import axios from 'axios';

const TaskAddList = ({ data }) => {
    const [veri, setVeri] = useState(data); // Initialize veri state with data prop or an empty array
    const apiUrl = 'https://v1.nocodeapi.com/gizem00001/google_sheets/xsWcTNrhFFLBFSqR?tabId=login';

    const handleDelete = async (task) => {
        console.log('Sil:', task);

        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            // Find the task to be deleted
            const deletedData = allData.find(item => item.Task === task);
            if (!deletedData) {
                throw new Error('Görev bulunamadı.'); // Throw an error if the task is not found
            }

            const row_id = deletedData.row_id; // Task ID (row_id)

            // Send DELETE request to remove the task from the database
            await axios.delete(`https://v1.nocodeapi.com/gizem00001/google_sheets/xsWcTNrhFFLBFSqR?tabId=login&row_id=${row_id}`);

            // Show success message
            message.success('Görev başarıyla silindi.');

            // Update the state to remove the deleted task
            setVeri(prevveri => prevveri.filter(item => item.Task !== task));
        } catch (error) {
            console.error('Görev silinirken hata oluştu:', error);
            message.error('Görev silinirken bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Görevleriniz:</h2>
            {veri && veri.length > 0 ? (
                veri.map((item, index) => (
                    <div key={index} style={{ marginBottom: '10px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Button style={{ flex: 1, marginRight: '10px' }} block>
                            {item.Task}
                        </Button>
                        <Popconfirm
                            title="Bu görevi silmek istediğinizden emin misiniz?"
                            onConfirm={() => handleDelete(item.Task)}
                            okText="Evet"
                            cancelText="Hayır"
                        >
                            <Button type="danger">Sil</Button>
                        </Popconfirm>
                    </div>
                ))
            ) : (
                <p>Yükleniyor...</p>
            )}
        </div>
    );
};

export default TaskAddList;
