import React from 'react';
import { Typography, Form, Input, Button, message } from 'antd';
import "./login.css";

const Login = () => {

    const handleLogin = (values) => {
        message.success("Başarılı");
        console.log('Received values:', values);
    }

    return (
        <div className="appBg">
            <Form className="loginForm" onFinish={handleLogin}>
            <Typography.Title style={{ textAlign: 'center' }}>Hoş Geldiniz</Typography.Title>

                <Form.Item 
                    label="Email" 
                    name="myEmail"
                    rules={[
                        {
                            required: true,
                            type: "email",
                            message: "Lütfen geçerli bir email adresi girin."
                        }
                    ]}
                >
                    <Input placeholder="E-mail girin" />
                </Form.Item>
                <Form.Item 
                    label="Şifre" 
                    name="myPassword"
                    rules={[
                        {
                            required: true,
                            message: "Lütfen şifrenizi girin."
                        }
                    ]}
                >
                    <Input.Password placeholder="Şifre girin" />
                </Form.Item>
                <Form.Item style={{ textAlign: 'center' }}>
    <Button type='primary' htmlType='submit' block>Giriş Yap</Button>
</Form.Item>


            </Form>
        </div>
    );
}

export default Login;
