import React, { useState, useEffect } from 'react';
import { Button, message } from 'antd';
import axios from 'axios';
import { MailOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined, FileAddOutlined } from '@ant-design/icons';
const Task = ({ userData }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false); // Yükleniyor durumunu tutacak state
  const [filteredTasks, setFilteredTasks] = useState([]);
  const apiUrl = 'https://v1.nocodeapi.com/gizem123/google_sheets/FQBdMrMXBbkmUYfs?tabId=login';

  const getData = async () => {
    try {
      const response = await axios.get(apiUrl);
      setData(response.data.data);
    } catch (error) {
      console.error('Failed to fetch data', error);
    }
  };

  useEffect(() => {
    getData(); // İlk renderda veriyi getir
  }, []);

  // Kullanıcının görevlerini filtrele ve state'e set et
  useEffect(() => {
    const filteredUsers = data.filter(item => item.UserID === userData.UserID);

    if (filteredUsers.length > 0) {
      const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
      setFilteredTasks(tasks);
    } else {
      setFilteredTasks([]);
    }
  }, [data, userData.UserID]); // useEffect'in bağımlılıkları: data ve userData.UserID

  const handleSubmit = async () => {
    setLoading(true); // Yükleme durumunu başlat

    try {
      const newData = [
        [
          userData.Email,
          userData.Password,
          userData.User,
          'New Task', // Yeni görev metni, bu kısmı dilediğiniz gibi değiştirebilirsiniz
          userData.UserID
        ]
      ];

      await axios.post(apiUrl, newData, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      // Yeni görev eklendikten sonra veriyi yeniden getir
      await getData();
      setLoading(false); // Yükleme durumunu kapat
    } catch (error) {
      console.error('Failed to add task:', error);
      message.error('Görev eklenirken hata oluştu!');
      setLoading(false); // Yükleme durumunu kapat
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Görevleriniz:</h2>
      {filteredTasks.length > 0 && (
        filteredTasks.map((task, index) => (
          <Button key={index} style={{ marginBottom: '10px'}} block>
            {task}
          </Button>
        ))
      )}
      {/* Yükleniyor durumunu göster */}
      {loading && <div style={{ marginTop: '10px' }}>Yükleniyor...</div>}

      {/* Ekleme butonu */}
      <Button
        type="primary"
        icon={<FileAddOutlined />}
        size="large"
        onClick={handleSubmit}
        style={{ marginTop: '10px' }}
      >
        Ekle
      </Button>
    </div>
  );
};

export default Task;
