import React, { useEffect, useState } from 'react';
import { Badge, Calendar, message, Space } from 'antd';
import axios from 'axios';
import 'antd/dist/antd.css'; // Ensure you import Ant Design CSS for styling

const colors = [
  'pink', 'red', 'yellow', 'orange', 'cyan', 'green', 'blue', 'purple', 'geekblue', 'magenta', 'volcano', 'gold', 'lime',
];

const TaskCalendar = ({ apiUrl, userData }) => {
  const [todos, setTodos] = useState([]);



  useEffect(() => {
    fetchDataAndFilterTasks();
  }, [apiUrl, userData]);

  const getListData = (value) => {
    const date = value.format('YYYY-MM-DD');
    return todos.filter(todo => todo.date === date);
  };

  const dateCellRender = (value) => {
    const listData = getListData(value);
    return (
      <ul className="events">
        {listData.map((item, index) => (
          <li key={index}>
            <Space direction="vertical">
              <Badge color={colors[index % colors.length]} text={item.task} />
            </Space>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <Calendar dateCellRender={dateCellRender} />
  );
};

export default TaskCalendar;
