import React, { useState, useRef, useEffect } from 'react';
import { Form, Input, Button, Checkbox, Row, Col, ConfigProvider, Modal, message, List } from 'antd';
import "./contact.css";
import axios from 'axios';
const { TextArea } = Input;

function AppContact({ userData }) {
  const [isModalVisible, setModalVisible] = useState(false);
  const formRef = useRef(null);
  const [showSubTask, setShowSubTask] = useState();
  const [tasks, setTasks] = useState([]);
  const [subTask, setSubTask] = useState([]);
  const apiUrl = 'https://v1.nocodeapi.com/gizem0345/google_sheets/zFKjRDAmBOXNnyQs?tabId=login';
  const openModal = () => {
    setModalVisible(true);
    formRef.current.resetFields();
  };
  const closeModal = () => {
    setModalVisible(false);
  };

  const onFinish = (values) => {
    console.log('Received values:', values);
    openModal(); // Form gönderildiğinde modalı aç
  };
  const fetchDataAndFilterTasks = async () => {
    try {
      const response = await axios.get(apiUrl);
      const allData = response.data.data;

      const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
      const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
      setTasks(tasks); // Set initial data
      console.log(tasks);

    } catch (error) {
      console.error('Failed to fetch data', error);
      message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
    }
  };

  // Fetch initial data on component mount
  useEffect(() => {
    fetchDataAndFilterTasks();
  }, []);
  const handleItemClick = async (task) => {
    try {
      const response = await axios.get(apiUrl);
      const allData = response.data.data;
      const filteredUsers = allData.filter(item => item.UserID === userData.UserID && item.Task === task);
      const subtasks = filteredUsers.flatMap(user => user.SubTask.split(',').map(SubTask => SubTask.trim()));
      setSubTask(subtasks);
      setShowSubTask(true);
      console.log("sub");
      console.log(subtasks);
    } catch (error) {
      console.error('Alt görevler alınırken hata oluştu:', error);
      message.error('Alt görev bulunamadı!');
    }
  };



  return (
    <ConfigProvider
      theme={{
        token: {
          colorPrimary: '#d11ba7',
          colorPrimaryHover: '#5b10b5', // Örnek bir renk tokenı
          colorText: "#d11ba7",
          //colorBgMask:"#d11ba7"
        },
      }}
    >
      <div id="contact" className="contact">
        <div className="contentHolder">
          <div>
            <div className="titleHolder">
              <h2>{userData.User} Görevleri</h2>
            </div>
            <Form
              name="contact_form"
              className="contact-form"
              initialValues={{
                remember: false,
                fullname: userData.User,
                email: userData.Email,
              }}
              onFinish={onFinish} // Form gönderildiğinde onFinish çağrılır
              ref={formRef}
            >
              <Row gutter={[16, 16]}>
                <Col span={12}>
                  <Form.Item
                    name="fullname"
                    rules={[{ required: true, message: 'Please enter your full name!' }]}
                  >
                    <Input placeholder="Full Name" />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="email"
                    rules={[
                      {
                        type: 'email',
                        message: 'Please enter a valid E-mail address!',
                      },
                      {
                        required: true,
                        message: 'Please input E-mail address!',
                      },
                    ]}
                  >
                    <Input placeholder="Email Address" />
                  </Form.Item>
                </Col>
              </Row>
              <div>
                <ul>
                  {tasks.map((task, index) => (
                    <li key={index} onClick={() => handleItemClick(task)}>
                      {task}
                    </li>
                  ))}
                </ul>
                {showSubTask && (
                <div style={{ marginLeft: '20px', marginTop: '10px' }}>
                    {subTask.map((subtask, index) => (
                        <Button key={index}>{subtask}</Button>
                    ))}
                </div>
            )}
              </div>


              <Form.Item
                name="remember"
                valuePropName="checked"
                noStyle
                rules={[
                  { validator: (_, value) => value ? Promise.resolve() : Promise.reject('Should accept agreement') },
                ]}
              >
                <Checkbox style={{ color: '#5b10b5' }}>Gönderdiğim verilerin doğruluğunu kabul ediyorum.</Checkbox>
              </Form.Item>
              <Form.Item>
                <Button type='primary' htmlType="submit" style={{ marginTop: '10px' }}>
                  Submit
                </Button>
              </Form.Item>
            </Form>
            <Modal
              centered
              visible={isModalVisible}
              onCancel={closeModal}
              cancelText="Close"
              closable={false}
              cancelButtonProps={{ type: 'primary' }}
              okButtonProps={{ style: { display: 'none' } }}
              wrapClassName="backdrop-backdrop-filter"
            >
              {isModalVisible && <h2 style={{ textAlign: "center", color: "white" }}>Sent Successfully!</h2>}
            </Modal>
          </div>
        </div>
      </div>
    </ConfigProvider>
  );
}

export default AppContact;
