import React from 'react';
import { Drawer, Button, Space } from 'antd';
import { PoweroffOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import {Avatar } from 'antd';
import { UserOutlined, MailOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined } from '@ant-design/icons';
const Draw = (props) => {
  const [placement, setPlacement] = React.useState('left'); // Default placement 'left'
  const navigate = useNavigate();
  const onChange = (e) => {
    setPlacement(e.target.value);
  };
  const handleLogout = () => {
    navigate(-1); // Navigate to previous page or perform logout action
  };

  return (
    <>
      <Drawer
        title={
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <Avatar size="large" icon={<UserOutlined />} /> {/* Replace with actual profile picture */}
              <span style={{ marginLeft: '10px' }}>User Profile</span> {/* Replace with user name or title */}
            </div>
          }
        placement={placement}
        closable={false} // Prevent closing the drawer
        mask={false} // Disable clicking outside to close
        onClose={() => {}} // No-op function to prevent closing on ESC key
        visible={true} // Always keep the drawer open
        key={placement}
      >
        <Space direction="vertical" style={{ width: '100%' }}>
          {props.itemsList.map((item) => (
            <React.Fragment key={item.key}>
              <Space>
                {item.icon}
                {item.label}
              </Space>
            </React.Fragment>
          ))}

          <Button
            key="logout"
            type="primary"
            icon={<PoweroffOutlined />}
            style={{ width: '50%', marginTop: 'auto' }}
            onClick={handleLogout}
          >
            Çıkış (Logout)
          </Button>
        </Space>
      </Drawer>
    </>
  );
};

export default Draw;
