import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Password from 'antd/es/input/Password';

const TaskEdit = ({userData}) => {
  const [taskInput, setTaskInput] = useState('');

  const handleTaskInputChange = (e) => {
    setTaskInput(e.target.value);
  };
  try {
    const apiUrl = 'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login';
    const data = {
        Email:userData.Email,
        Password:userData.Password,
        User:userData.Task,
        Task: taskInput,
        UserID:userData.UserID
      };

    // Veri nesnesi oluşturulur (örneğin, taskData burada kullanıcının girdiği görev olabilir)


    // Axios ile POST isteği gönderilir
    axios.post(apiUrl, data)
      .then(response => {
        console.log('İşlem başarılı:', response);
        alert("İşlem Başarılı " + response.status);
      })
      .catch(error => {
        console.error('İşlem sırasında bir hata oluştu:', error);
        alert("İşlem Başarısız " + error.message);
      });

  } catch (error) {
    console.error('İşlem sırasında bir hata oluştu:', error);
    alert("İşlem Başarısız " + error.message);
  }

  return(




    <div>
      <Form onFinish={handleAddTask}>
        <Form.Item name="taskInput">
          <Input
            placeholder="Görev Ekleyin"
            value={taskInput}
            onChange={handleTaskInputChange}
            size="large"
          />
        </Form.Item>
        <Form.Item>
          <Button htmlType="submit" type="primary" icon={<FileAddOutlined />} size="large">
            Ekle
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
}


export default TaskEdit;
