import React, { useState } from 'react';
import { Form, Input, Button } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Password from 'antd/es/input/Password';

const TaskForm = ({userData}) => {
    const [name, setName] = useState('');

    const handleChange = (e) => {
        setName(e.target.value);
    };
    const data=[
        {
            Email:userData.Email,
            Password:userData.Password,
            User:userData.User,
            Task:name,
            UserID:userData.UserID
        }
    ]

    const handleSubmit = async () => {
        try {
            const response = await axios.post('https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login', {
                data
            });
            console.log('Görev başarıyla eklendi:', response.data);
        } catch (error) {
            console.error('Görev eklenirken hata oluştu:', error);
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default TaskForm;
