import React, { useState } from "react";
import axios from 'axios';
import { Button, TimePicker, message, Popconfirm, ConfigProvider } from 'antd';
import { FileExcelOutlined, DownOutlined, UpOutlined } from '@ant-design/icons';
import moment from 'moment';

const { RangePicker } = TimePicker;

const TaskList = ({ task, deleteTask, userData, apiUrl }) => {
  const [subTask, setSubTask] = useState([]);
  const [showSubTask, setShowSubTask] = useState(false);
  const [clickedSubtaskIndex, setClickedSubtaskIndex] = useState(null);
  const [timeRanges, setTimeRanges] = useState({});

  const handleSubtaskClick = (index) => {
    setClickedSubtaskIndex(index);
  };

  const handleAddClick = async (index) => {
    if (timeRanges[index]) {
      const timeRangeString = `${timeRanges[index][0].format('HH:mm')}-${timeRanges[index][1].format('HH:mm')}`;
      const subtask = subTask[index];
      await updateTimeRangeInDatabase(subtask, timeRangeString);
      setClickedSubtaskIndex(null);
    } else {
      message.error('Lütfen önce bir zaman aralığı seçin.');
    }
  };



  const handleTimeRangeChange = (index, times) => {
    setTimeRanges({
      ...timeRanges,
      [index]: times,
    });
  };

  const handleDelete = async (task) => {
    try {
      const response = await axios.get(apiUrl);
      const allData = response.data.data;
      const deletedData = allData.find(item => item.Task === task && item.UserID === userData.UserID);

      if (!deletedData) {
        throw new Error('Görev bulunamadı.');
      }

      const row_id = deletedData.row_id;

      await axios.delete(`${apiUrl}&row_id=${row_id}`);

      message.success('Görev başarıyla silindi.');
      deleteTask(deletedData.Task);

    } catch (error) {
      console.error('Görev silinirken hata oluştu:', error);
      message.error('Görev silinirken bir hata oluştu. Lütfen tekrar deneyin.');
    }
  };
  const updateTimeRangeInDatabase = async (subtask, newTimeRange) => {
    try {
      // Veritabanından ilgili görevin verisini al
      const response = await axios.get(apiUrl);
      const allData = response.data.data;
      const taskData = allData.find(item => item.Task === task && item.UserID === userData.UserID);

      if (!taskData) {
        throw new Error('Görev bulunamadı.');
      }

      // Görevin alt görev listesinden ilgili alt görevin index'ini bul
      const subTaskList = taskData.SubTask.split(',').map(subTaskStr => subTaskStr.trim());
      const subtaskIndex = subTaskList.indexOf(subtask);

      if (subtaskIndex === -1) {
        throw new Error('Alt görev bulunamadı.');
      }


      const task = [
        [
          userData.Email,
          userData.Password,
          userData.User,
          userData.Telephone,
          userData.Address,
          userData.UserID,
          task,
          subtask,
          userData.Date,
          newTimeRange
        ]
      ];
      // Güncellenmiş zaman sütununu veritabanına gönder
      await axios.post(apiUrl,task)

      message.success('Zaman aralığı başarıyla eklendi.');

    } catch (error) {
      console.error('Zaman aralığı eklenirken hata oluştu:', error);
      message.error('Zaman aralığı eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
    }
  };


  const handleItemClick = async (task) => {
    try {
      const response = await axios.get(apiUrl);
      const allData = response.data.data;
      const filteredUsers = allData.filter(item => item.UserID === userData.UserID && item.Task === task);
      const subtasks = filteredUsers.flatMap(user => user.SubTask.split(',').map(SubTask => SubTask.trim()));
      setSubTask(subtasks);
      setShowSubTask(!showSubTask);
    } catch (error) {
      console.error('Alt görevler alınırken hata oluştu:', error);
      message.error('Alt görev bulunamadı!');
    }
  };

  return (
    <ConfigProvider
      theme={{
        token: {
          colorPrimary: '#d11ba7',
          colorPrimaryHover: '#5b10b5',
          colorIcon: 'black',
        },
      }}
    >
      <div style={{ display: "flex", flexDirection: "column" }}>
        <div style={{ display: "flex" }}>
          <Button
            style={{ color: "black", marginRight: '10px', width: '637px' }}
            onClick={() => handleItemClick(task)}
          >
            {task} {showSubTask ? <UpOutlined /> : <DownOutlined />}
          </Button>

          <Popconfirm
            title="Bu görevi silmek istediğinizden emin misiniz?"
            onConfirm={() => handleDelete(task)}
            okText="Evet"
            cancelText="Hayır"
          >
            <Button
              icon={<FileExcelOutlined />}
              type="primary"
              style={{ marginBlockEnd: "10px" }}
            >
              Sil
            </Button>
          </Popconfirm>
        </div>

        {showSubTask && (
          <div style={{ marginLeft: '20px' }}>
            {subTask.map((subtask, index) => (
              <div key={index} style={{ marginBottom: '10px', display: 'flex', alignItems: 'center' }}>
                <Button onClick={() => handleSubtaskClick(index)}>{subtask}</Button>
                {clickedSubtaskIndex === index && (
                  <div style={{ display: 'flex', alignItems: 'center', marginLeft: '10px' }}>
                    <RangePicker
                      style={{ marginLeft: '10px' }}
                      onChange={(times) => handleTimeRangeChange(index, times)}
                    />
                    <Button type="primary" style={{ marginLeft: '10px' }} onClick={() => handleAddClick(index)}>
                      Ekle
                    </Button>
                  </div>
                )}
                {timeRanges[index] && (
                  <span style={{ marginLeft: '10px' }}>
                    {timeRanges[index][0].format('HH:mm')} - {timeRanges[index][1].format('HH:mm')}
                  </span>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </ConfigProvider>
  );
}

export default TaskList;
