import React, { useState, useRef, useEffect } from 'react';
import { Form, Input, Button, message, Modal } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import { useNavigate ,useLocation} from 'react-router-dom';

const TaskForm = () => {
    const { state } = useLocation();
    const userData = state?.userData;
    const formRef = useRef(null);
    const [name, setName] = useState('');
    const [filteredTasks, setFilteredTasks] = useState([]);
    const [isModalVisible, setIsModalVisible] = useState(false); // Modal görünürlüğünü kontrol etmek için state
    const navigate = useNavigate();
    const apiUrl = 'https://v1.nocodeapi.com/gizem002/google_sheets/iaBQTHRWCbIvMRlI?tabId=login';

    // Verilerin ilk yüklenmesi ve güncellenmesi için kullanılan fonksiyon
    const fetchDataAndFilterTasks = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            // Kullanıcının verilerini filtrele
            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
            setFilteredTasks(tasks);
        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
        }
    };

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        const data = {
            Email: userData.Email,
            Password: userData.Password,
            User: userData.User,
            Task: name,
            UserID: userData.UserID
        };

        try {
            const response = await axios.post(apiUrl, data, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            formRef.current.resetFields(); // Formu temizle
            message.success('Başarıyla Eklendi');
            console.log('Görev başarıyla eklendi:', response.data);

            // Yeni görev ekledikten sonra verileri tekrar getir ve filtrele
            fetchDataAndFilterTasks();
            setIsModalVisible(false); // Modalı kapat
            navigate('/home/task', { state: { userData: userData, filteredTasks: filteredTasks } });
        } catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        }
    };

    const showModal = () => {
        setIsModalVisible(true); // Modalı göster
    };

    const handleCancel = () => {
        setIsModalVisible(false); // Modalı iptal et
    };

    const onLogout = () => {
        // Çıkış işlemlerini burada gerçekleştirin
        message.success('Çıkış yapıldı');
        navigate('/');
    };

    return (
        <div>
            <Button
                type="primary"
                icon={<FileAddOutlined />}
                size="large"
                onClick={showModal}
            >
                Görev Ekle
            </Button>
            <Button
                key="logout"
                type="text"
                style={{ width: '100%', textAlign: 'left', fontSize: '16px' }}
                onClick={onLogout}
            >
                Çıkış
            </Button>

            <Modal
                title="Görev Ekle"
                visible={isModalVisible}
                onCancel={handleCancel}
                footer={null}
            >
                <Form onFinish={handleSubmit} ref={formRef}>
                    <Form.Item name="taskInput">
                        <Input
                            name="görev"
                            placeholder="Görev Ekleyin"
                            value={name}
                            onChange={handleChange}
                            size="large"
                        />
                    </Form.Item>
                    <Form.Item>
                        <Button
                            htmlType="submit"
                            type="primary"
                            size="large"
                        >
                            Ekle
                        </Button>
                    </Form.Item>
                </Form>
            </Modal>
        </div>
    );
};

export default TaskForm;
