import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskData = ( userData ) => {
    const [data, setData] = useState([]);
    const [filteredTasks, setFilteredTasks] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizemz1234/google_sheets/QumnkHSFdwDvHcJy?tabId=login';

    const fetchDataAndFilterTasks = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;
            setData(allData);

            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            if (filteredUsers.length > 0) {
                const tasks = filteredUsers.flatMap(user => user.Task.map(task => task.trim()));
                setFilteredTasks(tasks);
            } else {
                setFilteredTasks([]);
            }
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        fetchDataAndFilterTasks();
    }, [userData.UserID]); // useEffect dependencies


    // Tüm görevleri ekrana gösterme işlemi

    return (
        {filteredTasks}


        );
};





export default TaskData;
