import React, { useState } from 'react';
import { Form, Input, Button, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';

const TaskEdit = ({ userData }) => {
    const [post, setPost] = useState({
        görev: ''  // Initial state should match the structure of your form data
    });

    const handleInput = (event) => {
        // Update state with the new value from the input
        setPost({ ...post, [event.target.name]: event.target.value });
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post(
            "https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm?tabId=login",
            post  // Send the post object directly, not wrapped in another object
        )
        .then(response => {
            console.log(response);
            // Optionally, show a success message or perform other actions upon success
            message.success('Görev başarıyla eklendi');
        })
        .catch(err => {
            console.error('Error adding task:', err);
            // Optionally, show an error message or perform other actions upon error
            message.error('Görev eklenirken bir hata oluştu');
        });
    }

    return (
        <div>
            <Form onFinish={handleSubmit}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        onChange={handleInput}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default TaskEdit;
