import React, { useState, useEffect, useRef } from 'react';
import { Form, Input, Button, message } from 'antd';
import axios from 'axios';

const TaskForm = ({ userData }) => {
    const formRef = useRef(null);
    const [name, setName] = useState('');
    const [filteredTasks, setFilteredTasks] = useState([]);
    const [updateFlag, setUpdateFlag] = useState(false); // Dummy state for forcing update
    const apiUrl = 'https://v1.nocodeapi.com/gizem004/google_sheets/rmLuKVNiJbmqpjdA?tabId=login';

    const fetchDataAndFilterTasks = async () => {
        try {
            const response = await axios.get(apiUrl);
            const allData = response.data.data;

            const filteredUsers = allData.filter(item => item.UserID === userData.UserID);
            const tasks = filteredUsers.flatMap(user => user.Task.split(',').map(task => task.trim()));
            setFilteredTasks(tasks);
        } catch (error) {
            console.error('Failed to fetch data', error);
            message.error('Veriler alınamadı. Lütfen daha sonra tekrar deneyin.');
        }
    };

    useEffect(() => {
        fetchDataAndFilterTasks();
    }, [updateFlag]); // Update fetchDataAndFilterTasks when updateFlag changes

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];

        try {
            const response = await axios.post(apiUrl, data, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            formRef.current.resetFields();
            setName(''); // Clear input field
            message.success('Başarıyla Eklendi');
            console.log('Görev başarıyla eklendi:', response.data);

            // Force update by toggling updateFlag
            setUpdateFlag(prevFlag => !prevFlag);
        } catch (error) {
            message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
            console.error('Görev eklenirken hata oluştu:', error);
        }
    };

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef}>
                <Form.Item name="taskInput">
                    <Input
                        name="görev"
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        size="large"
                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>

            <div style={{ padding: '20px' }}>
                <h2>Görevleriniz:</h2>
                {filteredTasks && filteredTasks.length > 0 ? (
                    filteredTasks.map((task, index) => (
                        <Button key={index} style={{ marginBottom: '10px' }} block>
                            {task}
                        </Button>
                    ))
                ) : (
                    "Yükleniyor..."
                )}
            </div>
        </div>
    );
};

export default TaskForm;
