import React, { useState, useEffect } from 'react';
import { Button } from 'antd';
import axios from 'axios';

const Task = ({ userData }) => {
  const [data, setData] = useState([]);
  const apiUrl = 'https://v1.nocodeapi.com/gizem12345/google_sheets/JheosOqLeKiZJKFm';

  const getData = async () => {
    try {
      const response = await axios.get(`${apiUrl}?tabId=login`);
      setData(response.data.data);
    } catch (error) {
      console.error('Failed to fetch data', error);
    }
  };

  useEffect(() => {
    getData(); // Fetch data
  }, []);

  const user = data.find(item => item.UserID === userData.UserID);

  if (!user) {
    return <div>Kullanıcı bulunamadı.</div>;
  }

  const tasks = user.Task.split(',').map(task => task.trim());

  return (
    <div style={{ padding: '20px' }}>
      <h2>Görevleriniz:</h2>
      {tasks.map((task, index) => (
        <Button key={index} style={{ marginBottom: '10px'}} block>
          {task}
        </Button>
      ))}
    </div>
  );
};

export default Task;
