import React, { useState, useEffect,useRef} from 'react';
import { Form, Input, Button,message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import Task from '../components/task';

const TaskForm = ({ userData }) => {
    const formRef = useRef(null);
    const [taskAdded, setTaskAdded] = useState(false);
    const [name, setName] = useState('');
    const [tasks, setTasks] = useState([]); // Görevlerin tutulduğu state
    const apiUrl='https://v1.nocodeapi.com/gizem123/google_sheets/FQBdMrMXBbkmUYfs?tabId=login';

    const handleChange = (e) => {
        setName(e.target.value);
    };

    const handleSubmit = async () => {
        const data = [
            [
                userData.Email,
                userData.Password,
                userData.User,
                name,
                userData.UserID
            ]
        ];

        try {
            const response = await axios.post(
                apiUrl,
                data,
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );
            message.success("Başarıyla Eklendi")
            setTaskAdded(true)
            console.log('Görev başarıyla eklendi:', response.data);


            // Görevler yeniden yüklenecek
            loadData(); // Yeniden veri çekme fonksiyonunu çağır
        } catch (error) {
            if (error.response) {
                // Sunucudan gelen yanıt var
                console.error('Görev eklenirken hata oluştu:', error.response.data);
            } else {
                // Yanıt yok, istek hiç yapılmadı ya da yapılamadı
                console.error('Görev eklenirken hata oluştu:', error.message);
            }
        }
    };

    // Görevleri API'den yeniden çekme
    const loadData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setTasks(response.data.data); // Görevleri state'e kaydet
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        loadData(); // Component yüklendiğinde ve güncellendiğinde görevleri çek
    }, []);

    return (
        <div>
            <Form onFinish={handleSubmit} ref={formRef}>
                <Form.Item
                    name="taskInput"
                    rules={[{ required: true, message: 'Lütfen bir görev girin!' }]}
                >
                    <Input
                        name='görev'
                        placeholder="Görev Ekleyin"
                        value={name}
                        onChange={handleChange}
                        size="large"
                    />
                </Form.Item>
                <Form.Item>
                    <Button
                        htmlType="submit"
                        type="primary"
                        icon={<FileAddOutlined />}
                        size="large"
                        onClick={(taskAdded)=>formRef.current.resetFields()}

                    >
                        Ekle
                    </Button>
                </Form.Item>
            </Form>
            {/* Sadece Task bileşenini tasks state'i değiştiğinde yeniden render et */}
            <Task userData={userData} ></Task>
        </div>
    );
};

export default TaskForm;
