import React, { useState, useRef } from 'react';
import { Form, Input, Button, DatePicker, message } from 'antd';
import { FileAddOutlined } from '@ant-design/icons';
import axios from 'axios';
import moment from 'moment';

const apiUrl = 'your-api-url'; // Replace with your actual API URL


const TaskForm = ({userData}) => {
  const [value, setValue] = useState('');
  const [subtask, setSubTask] = useState('');
  const [date, setDate] = useState(null);
  const formRef = useRef();

  const handleSubmit = async () => {
    if (value) {
      try {
        const taskData = [
          [
            userData.Email,
            userData.Password,
            userData.User,
            userData.Telephone,
            userData.Address,
            userData.UserID,
            value,
            subtask,
            date ? date.format('YYYY-MM-DD') : null
          ]
        ];

        // Post new task data
        await axios.post(apiUrl, taskData);

        // Reset form fields
        formRef.current.resetFields();
        message.success('Görev başarıyla eklendi.');
        // Add task to the state or perform any other action as needed
        addTask(value, subtask, date);

      } catch (error) {
        message.error('Görev eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
        console.error('Görev eklenirken hata oluştu:', error);
      }
    }
  };

  return (
    <Form onFinish={handleSubmit} ref={formRef} layout="inline">
      <Form.Item name="taskInput" style={{ flex: 1 }}>
        <Input
          name="görev"
          placeholder="Görev Ekleyin"
          size="large"
          value={value}
          onChange={(e) => setValue(e.target.value)}
        />
      </Form.Item>
      <Form.Item name="subtaskInput" style={{ flex: 1 }}>
        <Input
          name="alt görev"
          placeholder="Alt Görev Ekleyin"
          size="large"
          value={subtask}
          onChange={(e) => setSubTask(e.target.value)}
        />
      </Form.Item>
      <Form.Item name="dateInput" style={{ flex: 1 }}>
        <DatePicker
          placeholder="Tarih Seçin"
          size="large"
          value={date ? moment(date) : null}
          onChange={(date) => setDate(date)}
        />
      </Form.Item>
      <Form.Item>
        <Button
          icon={<FileAddOutlined />}
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginBlockEnd: '20px', marginRight: '2px' }}
        >
          Ekle
        </Button>
      </Form.Item>
    </Form>
  );
};

export default YourComponent;
