import React from 'react';
import { Drawer, Button, Space, Avatar } from 'antd';
import { PoweroffOutlined, UserOutlined } from '@ant-design/icons';
import { MailOutlined, SettingOutlined, FileTextOutlined, CalendarOutlined, FileAddOutlined } from '@ant-design/icons';
import LoginData from '../services/logindata';
import { useNavigate } from 'react-router-dom';
const itemsList = [
  {
    label: "Görevler",
    key: 'task',
    icon: <FileTextOutlined />
  },
  {
    label: "Görev Ekle",
    key: 'task2',
    icon: <FileAddOutlined />
  },
  {
    label: "Takvim",
    key: 'calendar',
    icon: <CalendarOutlined />
  },
  {
    label: 'Mail',
    key: 'mail',
    icon: <MailOutlined />,
  },
  {
    label: 'Profil Düzenle',
    key: 'setting:1',
    icon: <SettingOutlined />,
  }
];
const Draw = ({ userData }) => {
  const navigate = useNavigate();
  const onLogout = () => {
    navigate(-1); // Navigate to previous page or perform logout action
  };

  return (
    <Drawer
      title={
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar size="large" icon={<UserOutlined />} /> {/* Display user's profile picture */}
          <span style={{ marginLeft: '10px', fontSize: '18px', fontWeight: 'bold' }}>
          {userData.User}
          </span>
        </div>
      }
      placement="left"
      closable={false}
      mask={false}
      onClose={() => { }} // No-op function to prevent closing on ESC key
      visible={true} // Make sure this is controlled based on your application's state
    >
      <Space direction="vertical" style={{ width: '100%' }}>
        {itemsList.map((item) => (
          <Space key={item.key} style={{ marginBottom: '10px' }}>
            {React.cloneElement(item.icon, { style: { fontSize: '20px' } })}
            <span style={{ fontSize: '16px' }}>{item.label}</span>
          </Space>
        ))}

        <Button
          key="logout"
          type="primary"
          icon={<PoweroffOutlined />}
          style={{ width: '100%', marginTop: 'auto', fontSize: '16px',bcolor:"pink"}}
          onClick={onLogout}
        >
          Çıkış
        </Button>
      </Space>
    </Drawer>
  );
};

export default Draw;
