//Burada fonksiyonlar olacak.
import React, { useState } from "react";
import TaskForm from "../components/taskform";
const TaskWrapper=()=>{
    const [todos, setTodos] = useState([]);
    const addTask = (todo) => {
        setTodos([
          ...todos,todo,
        ]);
      }
      return(
        <div>
            <h1>Get Things Done !</h1>
            <TodoForm addTask={addTask} />
        </div>
      );

}
export default TaskWrapper;
