import React from 'react';

const Task = ({ userData }) => {
  // userData.Task dizesini virgüllerden ayırarak bir diziye dönüştür
  const tasks = userData.Task.split(',');

  return (
    <div>
      <h2>Tasks:</h2>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>{task.trim()}</li> // Her görevi bir liste öğesi olarak göster, trim() ile boşlukları temizle
        ))}
      </ul>
    </div>
  );
};

export default Task;
