import { useRef, useState, useEffect } from 'react';
import { message } from 'antd';
import axios from 'axios';

const LoginData = () => {
    const [isLoading, setIsLoading] = useState(false); 
    const formRef = useRef(null);
    const [data, setData] = useState([]);
    const apiUrl = 'https://v1.nocodeapi.com/gizem11/google_sheets/ICsbCoIOoLbnWrHj?tabId=login';

    const handleLogin = async (values) => {
        const { Email, myPassword } = values;

        // Check if the user exists in the fetched data
        const user = data.find(item => item.Email === Email && item.Password === myPassword);

        if (user) {
            message.success('Successful login',2);
            formRef.current.resetFields();
            setIsLoading(true);

            // Redirect to home page after successful login
            // Simulating the redirect with a timeout
            setTimeout(() => {
                setIsLoading(false); // Reset isLoading after redirection
                // Perform navigation here (e.g., using react-router-dom's useHistory or useNavigate)
            }, 1000); // Adjust timeout duration as needed
        } else {
            message.error('Incorrect email or password');
        }
    };

    const getData = async () => {
        try {
            const response = await axios.get(apiUrl);
            setData(response.data.data);
        } catch (error) {
            console.error('Failed to fetch data', error);
        }
    };

    useEffect(() => {
        getData();
    }, []);

    return { formRef, handleLogin, isLoading };
};

export default LoginData;
